(function (global, factory) {
    if (typeof define === "function" && define.amd) {
        define([], factory);
    } else if (typeof exports !== "undefined") {
        module.exports = factory();
    } else {
        global.Swal = factory();
    }
})(this, function () {
    return {
        fire: function (options) {
            // 기존 알림창 제거
            let existingPopup = document.querySelector(".swal-popup");
            if (existingPopup) {
                existingPopup.remove();
            }

            // 아이콘 종류 설정
            let iconMap = {
                success: "✅",
                error: "❌",
                warning: "⚠️",
                info: "ℹ️"
            };
            let icon = options.icon ? iconMap[options.icon] || "" : "";

            // 팝업 컨테이너 생성
            let popup = document.createElement("div");
            popup.className = "swal-popup";
            popup.innerHTML = `
                <div class="swal-content">
                    <div class="swal-icon">${icon}</div>
                    <h2 class="swal-title">${options.title || "알림"}</h2>
                    <p class="swal-text">${options.text || ""}</p>
                    <button class="swal-button">${options.confirmButtonText || "확인"}</button>
                </div>
            `;

            // 닫기 버튼 이벤트 추가
            popup.querySelector(".swal-button").onclick = function () {
                popup.classList.add("swal-hide");
                setTimeout(() => popup.remove(), 300);
            };

            document.body.appendChild(popup);

            // 애니메이션 효과 적용
            setTimeout(() => popup.classList.add("swal-show"), 10);
        }
    };
});